--

CREATE FUNCTION public.calculate_batch_abv() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF NEW."ActualOG" IS NOT NULL AND NEW."ActualFG" IS NOT NULL THEN
        -- ABV = (OG - FG) * 131.25
        NEW."ActualABV" := ROUND(((NEW."ActualOG" - NEW."ActualFG") * 131.25)::numeric, 2);
    END IF;
    RETURN NEW;
END;
$$;


--
-- Name: FUNCTION calculate_batch_abv(); Type: COMMENT; Schema: public; Owner: -
--

COMMENT ON FUNCTION public.calculate_batch_abv() IS 'Auto-calculates ABV from OG and FG measurements';


--
-- Name: calculate_measurement_range(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.calculate_measurement_range() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
BEGIN
    IF NEW."TargetMin" IS NOT NULL AND NEW."TargetMax" IS NOT NULL THEN
        NEW."IsInRange" := (NEW."Value" >= NEW."TargetMin" AND NEW."Value" <= NEW."TargetMax");

        -- Flag for review if out of range
        IF NOT NEW."IsInRange" THEN
            NEW."RequiresAction" := TRUE;
        END IF;
    END IF;
    RETURN NEW;
END;
$$;


--
-- Name: check_tenant_schema_health(uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.check_tenant_schema_health(p_tenant_id uuid) RETURNS TABLE(check_name character varying, status character varying, message text)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_schema_name VARCHAR(63);
    v_table_count INTEGER;
    v_expected_tables TEXT[] := ARRAY[
        'breweries', 'employees', 'customers', 'customer_reps', 'customer_visits',
        'beer_styles', 'recipes', 'equipment', 'equipment_service', 'products',
        'batches', 'batch_steps', 'inventory', 'inventory_counts', 'orders', 'order_line_items'
    ];
    v_table TEXT;
    v_table_exists BOOLEAN;
BEGIN
    -- Get schema name
    SELECT t.schema_name INTO v_schema_name
    FROM public.tenants t
    WHERE t.id = p_tenant_id AND t.is_active = true;

    IF v_schema_name IS NULL THEN
        RETURN QUERY SELECT 'tenant_exists'::VARCHAR(100), 'FAIL'::VARCHAR(20),
                           'Tenant not found or inactive'::TEXT;
        RETURN;
    END IF;

    -- Check if schema exists
    IF NOT EXISTS (
        SELECT 1 FROM information_schema.schemata
        WHERE schema_name = v_schema_name
    ) THEN
        RETURN QUERY SELECT 'schema_exists'::VARCHAR(100), 'FAIL'::VARCHAR(20),
                           format('Schema %s does not exist', v_schema_name)::TEXT;
        RETURN;
    END IF;

    RETURN QUERY SELECT 'schema_exists'::VARCHAR(100), 'PASS'::VARCHAR(20),
                       format('Schema %s exists', v_schema_name)::TEXT;

    -- Check expected tables
    FOREACH v_table IN ARRAY v_expected_tables LOOP
        SELECT EXISTS(
            SELECT 1 FROM information_schema.tables
            WHERE table_schema = v_schema_name AND table_name = v_table
        ) INTO v_table_exists;

        IF v_table_exists THEN
            RETURN QUERY SELECT
                format('table_%s', v_table)::VARCHAR(100),
                'PASS'::VARCHAR(20),
                format('Table %s.%s exists', v_schema_name, v_table)::TEXT;
        ELSE
            RETURN QUERY SELECT
                format('table_%s', v_table)::VARCHAR(100),
                'FAIL'::VARCHAR(20),
                format('Table %s.%s is missing', v_schema_name, v_table)::TEXT;
        END IF;
    END LOOP;

    -- Check total table count
    SELECT COUNT(*)::INTEGER INTO v_table_count
    FROM information_schema.tables
    WHERE table_schema = v_schema_name;

    RETURN QUERY SELECT 'table_count'::VARCHAR(100), 'INFO'::VARCHAR(20),
                       format('Schema contains %s tables (expected %s)', v_table_count, array_length(v_expected_tables, 1))::TEXT;
END;
$$;


