-- Name: copy_recipe_steps_to_batch(uuid, uuid, uuid); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.copy_recipe_steps_to_batch(p_recipe_id uuid, p_batch_id uuid, p_tenant_id uuid) RETURNS integer
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_step_count INTEGER := 0;
    v_recipe_step RECORD;
    v_new_batch_step_id UUID;
BEGIN
    -- Loop through recipe steps in order
    FOR v_recipe_step IN
        SELECT * FROM "RecipeStep"
        WHERE "RecipeId" = p_recipe_id
        ORDER BY "StepNumber"
    LOOP
        -- Create batch step from recipe step
        INSERT INTO "BatchStep" (
            "BatchId",
            "Name",
            "Description",
            "StepNumber",
            "StepType",
            "PlannedTemperature",
            "PlannedDuration",
            "Status",
            "Created",
            "Updated"
        ) VALUES (
            p_batch_id,
            v_recipe_step."StepName",
            v_recipe_step."Description",
            v_recipe_step."StepNumber",
            v_recipe_step."StepType",
            v_recipe_step."Temperature",
            v_recipe_step."Duration",
            'Not Started',
            CURRENT_TIMESTAMP,
            CURRENT_TIMESTAMP
        ) RETURNING "BatchStepId" INTO v_new_batch_step_id;

        v_step_count := v_step_count + 1;
    END LOOP;

    RETURN v_step_count;
END;
$$;


--
-- Name: create_broadcast_notification_status(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.create_broadcast_notification_status() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
DECLARE
    target_user_record RECORD;
    user_role TEXT;
BEGIN
    -- Only process broadcast notifications
    IF NEW."IsBroadcast" = true AND NEW."BroadcastRoles" IS NOT NULL THEN
        -- Find all users in the tenant with matching roles
        FOR target_user_record IN
            SELECT DISTINCT ut."UserId"
            FROM public."User_Tenant" ut
            WHERE ut."TenantId" = NEW."TenantId"
            AND ut."IsActive" = true
            AND ut."Role" = ANY(NEW."BroadcastRoles")
        LOOP
            -- Create notification status for each matching user
            INSERT INTO public."User_NotificationStatus" (
                "NotificationId", "UserId", "TenantId"
            ) VALUES (
                NEW."NotificationId", target_user_record."UserId", NEW."TenantId"
            ) ON CONFLICT ("NotificationId", "UserId") DO NOTHING;
        END LOOP;
    END IF;

    RETURN NEW;
END;
$$;


