CREATE FUNCTION public.get_available_equipment(p_tenant_id uuid, p_equipment_type_id uuid, p_start_datetime timestamp without time zone, p_end_datetime timestamp without time zone, p_min_capacity numeric DEFAULT NULL::numeric) RETURNS TABLE("EquipmentId" uuid, "Name" character varying, "Capacity" numeric, "CapacityUnit" character varying)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT
        e."EquipmentId",
        e."Name",
        e."Capacity",
        e."CapacityUnit"
    FROM "Equipment" e
    WHERE e."TenantId" = p_tenant_id
      AND e."EquipmentTypeId" = p_equipment_type_id
      AND e."Status" = 'Available'
      AND (p_min_capacity IS NULL OR e."Capacity" >= p_min_capacity)
      AND is_equipment_available(e."EquipmentId", p_start_datetime, p_end_datetime)
    ORDER BY e."Capacity" DESC;
END;
$$;


