    FROM "EquipmentSchedule"
    WHERE "EquipmentId" = p_equipment_id
      AND tsrange("StartDateTime", "EndDateTime") && tsrange(p_start_datetime, p_end_datetime);

    RETURN (v_conflict_count = 0);
END;
